import { Routes } from "@blitzjs/next"
import { useRouter } from "next/router"
import { useEffect } from "react"

function NavBar() {
  const router = useRouter()

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn")
    router
      .push(Routes.Login())
      .then((e) => console.log("Please login"))
      .catch((e) => console.log(e))
  }

  // const [isLoggedIn, setLoggedIn] = useState(false)
  useEffect(() => {
    //check if user is already logged in
    const userLoggedIn = localStorage.getItem("isLoggedIn")
    console.log(userLoggedIn)
    if (userLoggedIn !== "true") {
      router
        .push(Routes.Login())
        .then((e) => console.log("Please login"))
        .catch((e) => console.log(e))
    }
  }, [])

  return (
    <>
      <nav className="bg-white w-full z-20 top-0 left-0 border-b border-gray-200 ">
        <div className="max-w-screen-xl flex flex-wrap items-center justify-between mx-auto p-4">
          <img
            src="data:image/png;base64,"
            className="h-8 mr-3 cursor-pointer"
            alt="Flowbite Logo"
            onClick={() => router.push(Routes.Home())}
          ></img>
          <div className="flex md:order-2">
            <button
              type="button"
              onClick={handleLogout}
              className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2 text-center mr-3 md:mr-0"
            >
              Logout
            </button>
            <button
              data-collapse-toggle="navbar-sticky"
              type="button"
              className="inline-flex items-center p-2 text-sm text-gray-500 rounded-lg md:hidden hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-gray-200"
              aria-controls="navbar-sticky"
              aria-expanded="false"
            >
              <span className="sr-only">Open main menu</span>
              <svg
                className="w-6 h-6"
                aria-hidden="true"
                fill="currentColor"
                viewBox="0 0 20 20"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  fillRule="evenodd"
                  d="M3 5a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zM3 10a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zM3 15a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z"
                  clipRule="evenodd"
                ></path>
              </svg>
            </button>
          </div>
          <div
            className="items-center justify-between hidden w-full md:flex md:w-auto md:order-1"
            id="navbar-sticky"
          >
            <ul className="flex flex-col p-4 md:p-0 mt-4 font-medium border border-gray-100 rounded-lg bg-gray-50 md:flex-row md:space-x-8 md:mt-0 md:border-0 md:bg-white">
              <li
                className={`block py-2 pl-3 pr-4 rounded md:bg-transparent md:p-0 cursor-pointer
                    ${router.asPath === "/" ? "text-blue-700" : "text-gray-900"}`}
                onClick={() => router.push(Routes.Home())}
              >
                Home
              </li>
              <li
                onClick={() => router.push(Routes.Model())}
                className={`block py-2 pl-3 pr-4 text-gray-900 cursor-pointer rounded hover:bg-gray-100 md:hover:bg-transparent md:hover:text-blue-700 md:p-0
                ${router.asPath === "/Model" ? "text-blue-700" : "text-gray-900"}`}
              >
                Model
              </li>
              <li
                onClick={() => router.push(Routes.Locator())}
                className={`block py-2 pl-3 pr-4 text-gray-900 cursor-pointer rounded hover:bg-gray-100 md:hover:bg-transparent md:hover:text-blue-700 md:p-0
                ${router.asPath === "/Locator" ? "text-blue-700" : "text-gray-900"}`}
              >
                Results
              </li>
              <li
                onClick={() => router.push(Routes.Contact())}
                className={`block py-2 pl-3 pr-4 text-gray-900 cursor-pointer rounded hover:bg-gray-100 md:hover:bg-transparent md:hover:text-blue-700 md:p-0
                ${router.asPath === "/Contact" ? "text-blue-700" : "text-gray-900"}`}
              >
                Contact
              </li>
            </ul>
          </div>
        </div>
      </nav>
    </>
  )
}

export default NavBar
